import 'package:innaya_app/features/feature_auth/data/model/result_respone.dart';import 'dart:convert';import 'dart:io';import 'package:innaya_app/core/api_config.dart';import 'package:innaya_app/features/feature_auth/data/model/result_respone.dart';import 'package:innaya_app/features/feature_auth/data/model/user.dart';import 'package:innaya_app/localization/lang/message.dart';import 'package:http/http.dart' as http;import 'package:innaya_app/core/PreferenceUtils.dart';abstract class RemoteChangePass{  Future <ResultRespone>changePass(var body,int type);}class RemoteChangePassImpl implements RemoteChangePass{  @override  Future<ResultRespone> changePass(body,type)async {    ResultRespone mResultRespone;    // TODO: implement registerUser    Map<String, String> HeaderType;    var url;     if(type==1){       url = Uri.parse('${apiUrl}customer/forgot/password');     }else{       url = Uri.parse('${apiUrl}customer/password/update');     }    // var url = Uri.https(RootUrl, '${pathRootUrl}user/login');    //  url = RootUrl+"v1/user/login";    HeaderType = Header(0);    print(url);    var client = http.Client();    try {      var response = await client.post(        url,        headers: HeaderType,        body: jsonEncode(body),      );      print(response.statusCode);      if (response.statusCode == 200) {        var jsonDatapject = jsonDecode(response.body);        print(jsonDatapject);        await PreferenceUtils.setUser(User.fromJson(jsonDatapject['data']));        mResultRespone=ResultRespone(status: true, message: jsonDatapject['message']);        print(response.body);      } else {        var jsonDatapject = jsonDecode(response.body);        mResultRespone =            ResultRespone(status: false, message: jsonDatapject['message']);      }      print("Login");      print(response.body);      return mResultRespone;    } finally {      client.close();    }  }}