import 'package:dartz/dartz.dart';import 'package:innaya_app/core/error/exceptions.dart';import 'package:innaya_app/core/error/failure.dart';import 'package:innaya_app/core/network/network_info.dart';import 'package:innaya_app/features/feature_auth/data/dataresourse/remote_verfication_account.dart';import 'package:innaya_app/features/feature_auth/data/model/result_respone.dart';import 'package:innaya_app/features/feature_auth/domain/repostriy/repostriy_verfication.dart';class VerficationAccountRepostriyImpl implements RepostriyVerfication{  NetWorkInfo? netWorkInfo;  RemoteVerficationAccount? remoteVerficationAccount;  VerficationAccountRepostriyImpl(      {this.netWorkInfo, this.remoteVerficationAccount});  @override  Future<Either<Failure, ResultRespone>> verficationAccount(Body)async {    bool isConnect=await netWorkInfo!.isConnectNetwork;    if(isConnect){      try{       final result= await remoteVerficationAccount!.verficationAccount(Body);       return Right(result);      }catch(e){        return  Left(ServerFailure());      }    }else{      return Left(ServerFailure());    }    // TODO: implement verficationAccount  }}