import 'package:dartz/dartz.dart';import 'package:innaya_app/core/error/failure.dart';import 'package:innaya_app/core/usecases/params.dart';import 'package:innaya_app/core/usecases/params_map.dart';import 'package:innaya_app/core/usecases/usecases.dart';import 'package:innaya_app/features/feature_auth/data/model/result_respone.dart';import 'package:innaya_app/features/feature_auth/data/model/user.dart';import 'package:innaya_app/features/feature_auth/domain/repostriy/repostriy_login.dart';import 'package:innaya_app/features/feature_auth/domain/repostriy/repostriy_register.dart';class loginUser extends UseCase<ResultRespone,Params> {  RepostriyLogin ?repostriyLogin;  loginUser({this.repostriyLogin});  @override  Future<Either<Failure, ResultRespone>> call(parmas) async {    return await repostriyLogin!.loginUser(parmas.body!);  }}