import 'dart:io';import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/svg.dart';import 'package:innaya_app/core/app_size.dart';class ItemChooseImage extends StatelessWidget {    File ?fileImage;    GestureTapCallback ?pressChooseImage;    String imageUrl;   ItemChooseImage({Key? key,this.fileImage,this.pressChooseImage,this.imageUrl=""}) : super(key: key);  @override  Widget build(BuildContext context) {    ScreenUtil.init(     context                    ,      designSize: Size(SCREEN_WIDTH, SCREEN_HIGHT),    );    return Container(      width: 120.w,      height: 120.h,      child: Stack(        children: [          ClipOval(            child: Container(                width: 110.w,                height: 110.h,                child: CachedNetworkImage(                  imageUrl:imageUrl,                  imageBuilder: (context, imageProvider) {                    return  GestureDetector(                      onTap: (){                      },                      child: Container(                        width: double.infinity,                        height: double.infinity,                        decoration: BoxDecoration(                          image:                          DecorationImage(image: imageProvider, fit: BoxFit.cover),                        ),                      ),                    );                  },                  placeholder: (context, url) => Container(                    alignment: Alignment.center,                    width: double.infinity,                    height: double.infinity,                    child: Image.asset(                      'assets/icons/loading.gif',                      width: 20,                      height: 20,                    ),                  ),                  errorWidget: (context, url, error) => (fileImage!=null)?Image.file(                    fileImage!,                    width: 110.w,                    height: 110.h,                    fit: BoxFit.cover,                  ):SvgPicture.asset(                    'assets/icons/avatar.svg',                    width: 110.w,                    height: 110.h,                  ),                )),),          Positioned.directional(              bottom: 10,              textDirection:Directionality.of(context),              end:10,              child:GestureDetector(                onTap: pressChooseImage,                child: SvgPicture.asset(                  'assets/icons/choos_image.svg',                  width: 45,                  height: 45,                ),              )          ),        ],      ),    );  }}